mapper.select:
	SELECT SIMUL_NO,
		   CRP_NO,
		   AVG(VAL) AS AGGR_VAL
	  FROM SHRP_${indicator}
	 WHERE SIMUL_NO = #{simulationNumber}
  GROUP BY CRP_NO;

SELECT SIMUL_NO,
	   CRP_NO,
	   DRC_CD,
	   VAL
  FROM SHRP_${indicator}
 WHERE SIMUL_NO = #{simulationNumber}
   AND CRP_NO = #{crossRoadNumber}
